import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';



function loadTweets(tweet){
  const xhr = new XMLHttpRequest();
  const method = 'GET';
  const url = "/tweets"; 
  const responseType = "json";

  xhr.responseType = responseType;
  xhr.open(method, url);
  xhr.onload = function(){
      const serverResponse = xhr.response;
      const listedItems = serverResponse;
      let finalTweetStr = "";
      for (let i = 0; i < listedItems.length; i++){
          const tweetObj = listedItems[i];
          const currentTweet = formatTweetElement(tweetObj);
          finalTweetStr += currentTweet;
      }
      tweet.innerHTML = finalTweetStr; 
  }
  xhr.send();
}

function App() {
  const [tweets, setTweets] = useState([{content: '123'}])
  useEffect(() => {
    const tweetItems = [{"content": 123}, {"content": "Hello world" }]
    setTweets(tweetItems) 
  }, [])
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          {tweets.map((tweet, index) => {
            return <li>{tweet.content}</li>
            })
          }
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
